const userReducerInitialState={}

const userReducer=(state=userReducerInitialState,action)=>{
    switch(action.type){
        case "LOGIN_USER":
            return {...state,user:action.payload};
        case "LOGOUT_USER":
            return {...state,user:action.payload}
        default:
            return state;
    }

}
export default userReducer
